package com.bizarre.durationcounter;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Toast;import androidx.annotation.Nullable;import androidx.appcompat.app.AppCompatActivity;import com.bizarre.durationcounter.databinding.ActivityAddEventBinding;import com.google.android.material.floatingactionbutton.FloatingActionButton;import com.google.android.material.textfield.TextInputLayout;public class AddEventActivity extends AppCompatActivity {    private ActivityAddEventBinding binding;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        binding = ActivityAddEventBinding.inflate(getLayoutInflater());        setContentView(binding.getRoot());        setSupportActionBar(binding.toolbar);        getSupportActionBar().setDisplayHomeAsUpEnabled(true);        Intent intent = getIntent();        int eventType = intent.getIntExtra("eventType", 0);        if(eventType == 1){            binding.toDateTextLayout.setVisibility(View.GONE);        }        FloatingActionButton saveEvent = binding.floatingActionButton.mainFab;        saveEvent.setImageResource(R.drawable.baseline_calendar_today_24);        saveEvent.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Toast.makeText(getApplicationContext(), "Event Saved!!", Toast.LENGTH_SHORT).show();            }        });    }}